document.addEventListener('DOMContentLoaded',function(){const accordionButtons=document.querySelectorAll('.accordion-button');accordionButtons.forEach(button=>{button.addEventListener('click',function(){this.classList.toggle('active');const content=this.nextElementSibling;content.classList.toggle('active');});});const cleaningForm=document.getElementById('cleaning-form');cleaningForm.addEventListener('submit',function(e){e.preventDefault();const formData=new FormData(cleaningForm);const formDataObj={};formData.forEach((value,key)=>{if(formDataObj[key]){if(!Array.isArray(formDataObj[key])){formDataObj[key]=[formDataObj[key]];}
formDataObj[key].push(value);}else{formDataObj[key]=value;}});console.log('Form data:',formDataObj);window.location.href='https://bookingkoala.com/cleanuity';});document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();const targetId=this.getAttribute('href');if(targetId==='#')return;const targetElement=document.querySelector(targetId);if(targetElement){targetElement.scrollIntoView({behavior:'smooth',block:'start'});}});});const fadeElements=document.querySelectorAll('.feature, .step, .testimonial');const fadeInOptions={threshold:0.3,rootMargin:'0px 0px -100px 0px'};const fadeInObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity='1';entry.target.style.transform='translateY(0)';observer.unobserve(entry.target);}});},fadeInOptions);fadeElements.forEach(element=>{element.style.opacity='0';element.style.transform='translateY(20px)';element.style.transition='opacity 0.5s ease, transform 0.5s ease';fadeInObserver.observe(element);});const formInputs=document.querySelectorAll('input, select, button');formInputs.forEach(input=>{if(window.innerWidth<768){input.style.padding='12px';}});});
